mapper.select:



select info.order_no,info.member_id,info.order_notes,info.receive_user,info.receive_address,info.platform_create_time,info.commit_time,
info.receive_post,info.receive_tel,info.province_name,info.city_name,info.area_name,info.invoice_title,info.invoice_content
,IF(info.order_type=1,'补发订单','正常订单')  as order_type ,IF(info.is_pay='Y','已付款','未付款') as is_pay,info.payment_type,info.multi_channel_order_no,info.multi_channel_id,
item.goods_no_69,item.name,item.standard,item.order_no,item.goods_no from order_info info right join  order_item item on info.order_no = item.order_no


      and info.platform_create_time between #{start} and #{end}


      and info.order_status = #{order_status}


      and info.order_type = #{order_type}


      and info.multi_channel_id between #{multi_channel_id1} and #{multi_channel_id2}

   	and item.goods_list_type='GENERAL'

order by info.add_time desc limit #{startOffset} , #{pageSize}

select count(distinct info.order_no) from order_info info right join  order_item item on info.order_no = item.order_no


     and info.platform_create_time between #{start} and #{end}


     and info.order_status = #{order_status}


     and info.order_type = #{order_type}


     and info.multi_channel_id between #{multi_channel_id1} and #{multi_channel_id2}
